pragma solidity ^0.6.6;

contract Sorter {

    uint256[] public sortedArray;
    function setSort(uint256[] memory _data) public { sortedArray = _data; }
    function sort() public {
        if (sortedArray.length == 0)
            return;
        quickSort(sortedArray, 0, sortedArray.length - 1);
    }

    function quickSort(uint256[] storage arr, uint left, uint right) internal {
        uint i = left;
        uint j = right;
        uint pivot = arr[left + (right - left) / 2];
        while (i <= j) {
            while (arr[i] < pivot) i++;
            while (pivot < arr[j]) j--;
            if (i <= j) {
                (arr[i], arr[j]) = (arr[j], arr[i]);
                i++;
                j--;
            }
        }
        if (left < j)
            quickSort(arr, left, j);

        if (i < right)
            quickSort(arr, i, right);
    }
}
